import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Light as SyntaxHighlighter } from 'react-syntax-highlighter';
import { github } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import './App.css';

// Import language support
import javascript from 'react-syntax-highlighter/dist/esm/languages/hljs/javascript';
import python from 'react-syntax-highlighter/dist/esm/languages/hljs/python';
import java from 'react-syntax-highlighter/dist/esm/languages/hljs/java';
import go from 'react-syntax-highlighter/dist/esm/languages/hljs/go';
import css from 'react-syntax-highlighter/dist/esm/languages/hljs/css';
import xml from 'react-syntax-highlighter/dist/esm/languages/hljs/xml';
import markdown from 'react-syntax-highlighter/dist/esm/languages/hljs/markdown';

// Register languages
SyntaxHighlighter.registerLanguage('javascript', javascript);
SyntaxHighlighter.registerLanguage('python', python);
SyntaxHighlighter.registerLanguage('java', java);
SyntaxHighlighter.registerLanguage('go', go);
SyntaxHighlighter.registerLanguage('css', css);
SyntaxHighlighter.registerLanguage('xml', xml);
SyntaxHighlighter.registerLanguage('markdown', markdown);

function App() {
  const [repoData, setRepoData] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchRepoData();
  }, []);

  const fetchRepoData = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:8080/repo');
      setRepoData(response.data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch repository data');
      setLoading(false);
    }
  };

  const handleFileClick = (file) => {
    setSelectedFile(file);
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (error) {
    return <div className="error">{error}</div>;
  }

  return (
    <div className="app">
      <header className="header">
        <h1>Finternship File Viewer</h1>
        {repoData && (
          <div className="repo-info">
            <span>{repoData.githubUsername}</span>
            <span>/</span>
            <span>{repoData.repoName}</span>
          </div>
        )}
      </header>

      <div className="content">
        <div className="file-list">
          <h2>Files</h2>
          {repoData?.files.map((file, index) => (
            <div
              key={index}
              className={`file-item ${selectedFile === file ? 'selected' : ''}`}
              onClick={() => handleFileClick(file)}
            >
              {file.path}
            </div>
          ))}
        </div>

        <div className="file-viewer">
          {selectedFile ? (
            <>
              <div className="file-header">
                <h3>{selectedFile.path}</h3>
              </div>
              <div className="code-container">
                <SyntaxHighlighter
                  language={selectedFile.language}
                  style={github}
                  showLineNumbers
                  customStyle={{
                    margin: 0,
                    borderRadius: '0 0 4px 4px',
                  }}
                >
                  {selectedFile.content}
                </SyntaxHighlighter>
              </div>
            </>
          ) : (
            <div className="no-file">
              Select a file from the list to view its contents
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
